object PickerUtils {
    fun birthdayPicker(context: Context, result: (String) -> Unit) {
        //时间选择器
        val pvTime = TimePickerBuilder(
            context
        ) { date, v ->
            result(date2String(date))
        }.setSubmitText(context.resources.getString(R.string.confirm))
            .setCancelText(context.resources.getString(R.string.cancel))
            .setLabel("", "", "", "", "", "")
            .setType(booleanArrayOf(true, true, true, false, false, false))
            .build()
        pvTime.show()
    }

    /**
     * Date2String
     */
    fun date2String(date: Date): String {
        var format = SimpleDateFormat("yyyy-MM-dd")
        return format.format(date)
    }


